            canvas.freeDrawingBrush.color = '#000';

            storm.xOffset = util.getOffset(document.getElementById('canvasId')).left+storm.xOffset;
            storm.yOffset = util.getOffset(document.getElementById('canvasId')).top+ storm.yOffset;

            mfabric.initialize();
