        if(recvdObj.latex)
            obj.latex=recvdObj.latex;
        if(recvdObj.type)
            obj.type=recvdObj.type;
        if(recvdObj.showEditIcon)
            obj.showEditIcon=recvdObj.showEditIcon;

		if (recvdObj.fill) {
			obj.set("fill", recvdObj.fill);
		}
		if (recvdObj.stroke) {
			obj.set("stroke", recvdObj.stroke);
		}
		if (obj.text) {
			obj.text = recvdObj.text;
		}
